INSERT INTO `config_info` VALUES (16, 'application-dev.yml', 'DEFAULT_GROUP', '# 加解密根密码\njasypt:\n  encryptor:\n    password: albedo #根密码\n\n\ndubbo:\n  scan:\n    base-packages: com.albedo.java.*\n  protocols:\n    dubbo:\n      name: dubbo\n      port: -1\n  registry:\n    #   The Spring Cloud Dubbo\'s registry extension\n    ##  the default value of dubbo-provider-services is \"*\", that means to subscribe all providers,\n    ##  thus it\'s optimized if subscriber specifies the required providers.\n    address: spring-cloud://localhost\n  cloud:\n    # The subscribed services in consumer side\n    subscribed-services: albedo-sys\n\n# Spring 相关\nspring:\n  redis:\n    password:\n    host: albedo-redis\n  cloud:\n    sentinel:\n      eager: true\n      transport:\n        dashboard: albedo-sentinel:8858\n\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n  endpoint:\n    health:\n      show-details: always\n\n# feign 配置\nfeign:\n  hystrix:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 10000\n        readTimeout: 10000\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n      useGzipDecoder: true\n\n#请求处理的超时时间\nribbon:\n  ReadTimeout: 10000\n  ConnectTimeout: 10000\n\n# mybaits-plus配置\nmybatis-plus:\n  mapper-locations: classpath:/mapper/*/*Mapper.xml\n  global-config:\n    banner: false\n    db-config:\n      id-type: UUID\n      field-strategy: NOT_NULL\n      table-underline: true\n      logic-delete-value: 1\n      logic-not-delete-value: 0\n  configuration:\n    map-underscore-to-camel-case: true\n\n# spring security 配置\nsecurity:\n  oauth2:\n    resource:\n      loadBalanced: true\n      token-info-uri: http://albedo-auth/oauth/check_token\n    # 通用放行URL，服务个性化，请在对应配置文件覆盖\n    ignore:\n      urls:\n        - /v2/api-docs\n        - /actuator/**\n\n# ===================================================================\n# Albedo specific properties\n# ===================================================================\n\napplication:\n  developMode: true\n  address-enabled: true\n  logPath: logs\n  static-file-directory:\n    mac: ~/albedo-file\n    linux: /home/albedo/file/\n    win: C:\\albedo\\file\\\n#密码加密传输，前端公钥加密，后端私钥解密\n  rsa:\n    public-key: MFwwDQYJKoZIhvcNAQEBBQADSwAwSAJBAN4UOrkIuLpL0o7WItgIUkP/RFBsurMPQ7fTaOKwT+S9tWly0xMmJzSl9Kdh8MpWcyz+5nUSb7SgGWxiE3qIL2sCAwEAAQ==\n    private-key: \n  cors: #By default CORS are not enabled. Uncomment to enable.\n    allowed-origins: \"*\"\n    allowed-methods: \"*\"\n    allowed-headers: \"*\"\n    exposed-headers: \"Authorization,Link,X-Total-Count\"\n    allow-credentials: true\n    max-age: 1800\n  swagger:\n    title: Albedo Swagger API\n    license: Powered By somewhere\n    licenseUrl: https://github.com/somowhere\n    terms-of-service-url: https://github.com/somowhere\n    contact:\n      email: somewhere0813@gmail.com\n      url: https://github.com/somowhere\n    authorization:\n      name: Albedo OAuth\n      auth-regex: ^.*$\n      authorization-scope-list:\n        - scope: server\n          description: server all\n      token-url-list:\n        - http://${GATEWAY_HOST:albedo-gateway}:${GATEWAY-PORT:9999}/auth/oauth/token', 'ec4f549443841da54e5faaf9799765f0', '2019-12-14 10:14:03', '2020-07-06 21:43:28', NULL, '0:0:0:0:0:0:0:1', '', '', '通用配置', 'null', 'null', 'yaml', 'null');

