INSERT INTO `his_config_info` VALUES (10, 12, 'albedo-gateway-dev.yml', 'DEFAULT_GROUP', '', 'spring:\r\n  cloud:\r\n    gateway:\r\n      locator:\r\n        enabled: true\r\n      routes:\r\n        # 认证中心\r\n        - id: albedo-auth\r\n          uri: lb://albedo-auth\r\n          predicates:\r\n            - Path=/auth/**\r\n          filters:\r\n            # 验证码处理\r\n            - ValidateCodeGatewayFilter\r\n            # 前端密码解密\r\n            - PasswordDecoderFilter\r\n        #系统管理 模块\r\n        - id: albedo-sys\r\n          uri: lb://albedo-sys\r\n          predicates:\r\n            - Path=/sys/**\r\n          filters:\r\n            # 限流配置\r\n            - name: RequestRateLimiter\r\n              args:\r\n                key-resolver: \'#{@remoteAddrKeyResolver}\'\r\n                redis-rate-limiter.replenishRate: 10\r\n                redis-rate-limiter.burstCapacity: 20\r\n              # 降级配置\r\n        #            - name: Hystrix\r\n        #              args:\r\n        #                name: default\r\n        #                fallbackUri: \'forward:/fallback\'\r\n        # 代码生成模块\r\n        - id: albedo-gen\r\n          uri: lb://albedo-gen\r\n          predicates:\r\n            - Path=/gen/**\r\n        # 任务调度模块\r\n        - id: albedo-quartz\r\n          uri: lb://albedo-quartz\r\n          predicates:\r\n            - Path=/quartz/**\r\n\r\n    sentinel:\r\n      # datasource.ds2.file:\r\n      #   file: \"classpath: gateway.json\"\r\n      #   ruleType: gw-flow\r\n      # datasource.ds1.file:\r\n      #   file: \"classpath: api.json\"\r\n      #   ruleType: gw-api-group\r\n      transport:\r\n        dashboard: 127.0.0.1:8858\r\n      filter:\r\n        enabled: true\r\n      scg.fallback:\r\n          mode: response\r\n          response-status: 444\r\n          response-body: 1234\r\n      scg:\r\n        order: -100\r\nsecurity:\r\n  encode:\r\n    # 前端密码密钥，必须16位\r\n    key: \'somewhere-albedo\'\r\n\r\n# 不校验验证码终端\r\nignore:\r\n  clients:\r\n    - swagger\r\n', '31ba0fa6c588872cf0fa56956df0008d', '2010-05-05 00:00:00', '2020-06-25 17:41:41', NULL, '0:0:0:0:0:0:0:1', 'U', '');
